import React, { useEffect, useState } from "react";
import "./UserDashboard.css";

const essentialsKit = [
  "1. Food & Water Bowls",
  "2. High-Quality Pet Food",
  "3. Leash & Collar (for dogs) / Harness (for cats)",
  "4. Comfortable Bed or Blanket",
  "5. Toys (Chew Toys, Interactive Toys, etc.)",
  "6. Grooming Kit (Brush, Shampoo, Nail Clippers)",
  "7. Poop Bags & Waste Scooper / Litter Box & Litter (for cats)",
  "8. Training Pads or Pee Pads",
  "9. First Aid Kit",
  "10. Crate or Travel Carrier",
];

const pickupAddress = {
  name: "Happy Tails Adoption Center",
  street: "123 Paw Street, Koregaon Park",
  city: "Pune, Maharashtra - 411001",
  contact: "+91 98765 43210",
  timings: "10:00 AM - 6:00 PM",
};

const petVaccines = [
  "1. Rabies Vaccine",
  "2. Distemper Vaccine",
  "3. Parvovirus Vaccine",
  "4. Leptospirosis Vaccine",
  "5. Bordetella Vaccine",
  "6. Feline Leukemia Vaccine",
];

const UserDashboard = () => {
  const [requests, setRequests] = useState([]);
  const [selectedPet, setSelectedPet] = useState(null);
  const [showPickupDetails, setShowPickupDetails] = useState(false);
  const [showVaccineDetails, setShowVaccineDetails] = useState(false);
  const [showFeedbackForm, setShowFeedbackForm] = useState(false);
  const [feedbackData, setFeedbackData] = useState({
    owner: "",
    pet: "",
    message: "",
    rating: 5,
    image: null,
  });
  const [thankYou, setThankYou] = useState(false);

  const userId = localStorage.getItem("userId");

  useEffect(() => {
    if (!userId) return;

    const fetchRequests = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/adoption-requests/user/${userId}`);
        const data = await res.json();
        setRequests(data);
      } catch (err) {
        console.error("Error fetching requests:", err);
      }
    };

    fetchRequests();
    const interval = setInterval(fetchRequests, 5000);
    return () => clearInterval(interval);
  }, [userId]);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFeedbackData((prev) => ({ ...prev, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleFeedbackSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted Feedback:", feedbackData);
    setThankYou(true);
    setTimeout(() => {
      setShowFeedbackForm(false);
      setThankYou(false);
      setFeedbackData({ owner: "", pet: "", message: "", rating: 5, image: null });
    }, 2000);
  };

  return (
    <div className="user-dashboard">
      <h2>Your Adoption Requests</h2>
      {requests.length === 0 ? (
        <p>No requests yet.</p>
      ) : (
        <div className="requests-list">
          {requests.map((req) => (
            <div className="request-item" key={req._id} style={{ position: "relative" }}>
              <img src={req.petImage || "/images/pets/default.jpeg"} className="request-pet-image" />
              <div>
                <h3>{req.petName}</h3>
                <p><strong>Reason:</strong> {req.reason}</p>
                <p><strong>Status:</strong> <span className={`status ${req.status}`}>{req.status}</span></p>
                {req.status === "approved" && (
                  <p className="success-message">üéâ Congratulations! Your adoption request has been approved.</p>
                )}
                {req.status === "rejected" && (
                  <p className="error-message">‚ùå Unfortunately, your request was not approved.</p>
                )}
                {req.status === "approved" && (
                  <div className="action-buttons">
                    <button className="btn" onClick={() => setSelectedPet(req)}>Pet Essential Kit</button>
                    <button className="btn" onClick={() => setShowVaccineDetails(true)}>Pet Vaccine Details</button>
                    <button className="btn" onClick={() => setShowPickupDetails(true)}>Pet Pickup Details</button>
                    <button
                      className="btn"
                      onClick={() => setShowFeedbackForm(true)}
                      style={{ backgroundColor: "#5a3531", color: "white", marginLeft: "10px" }}
                    >
                      üìù Give Feedback
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}

      {selectedPet && (
        <div className="modal-overlay" onClick={() => setSelectedPet(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Essential Kit for {selectedPet.petName}</h3>
            <ul>
              {essentialsKit.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
            <button className="btn close-btn" onClick={() => setSelectedPet(null)}>Close</button>
          </div>
        </div>
      )}

      {showVaccineDetails && (
        <div className="modal-overlay" onClick={() => setShowVaccineDetails(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Pet Vaccine Details</h3>
            <ul>
              {petVaccines.map((vaccine, index) => (
                <li key={index}>{vaccine}</li>
              ))}
            </ul>
            <button className="btn close-btn" onClick={() => setShowVaccineDetails(false)}>Close</button>
          </div>
        </div>
      )}

      {showPickupDetails && (
        <div className="modal-overlay" onClick={() => setShowPickupDetails(false)} style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", backgroundColor: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center", zIndex: 1000 }}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()} style={{ background: "#fff", padding: "25px", borderRadius: "10px", textAlign: "center", maxWidth: "400px", width: "90%" }}>
            <h3>Pet Pickup Details</h3>
            <p><strong>üìç {pickupAddress.name}</strong></p>
            <p>{pickupAddress.street}</p>
            <p>{pickupAddress.city}</p>
            <p><strong>üìû Contact:</strong> {pickupAddress.contact}</p>
            <p><strong>üïí Timings:</strong> {pickupAddress.timings}</p>
            <button className="btn close-btn" onClick={() => setShowPickupDetails(false)} style={{ marginTop: "15px", padding: "8px 16px", backgroundColor: "#5a3531", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}>Close</button>
          </div>
        </div>
      )}

      {showFeedbackForm && (
        <div className="modal-overlay" onClick={() => setShowFeedbackForm(false)} style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", backgroundColor: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center", zIndex: 1000 }}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()} style={{ background: "#fff", padding: "20px", borderRadius: "10px", width: "400px", textAlign: "center" }}>
            {thankYou ? (
              <h3>üéâ Thank you for your feedback!</h3>
            ) : (
              <form onSubmit={handleFeedbackSubmit}>
                <h3>Give Feedback</h3>
                <input type="text" placeholder="Owner Name" value={feedbackData.owner} onChange={(e) => setFeedbackData({ ...feedbackData, owner: e.target.value })} style={{ marginBottom: "10px", padding: "8px", width: "100%" }} required />
                <input type="text" placeholder="Pet Name" value={feedbackData.pet} onChange={(e) => setFeedbackData({ ...feedbackData, pet: e.target.value })} style={{ marginBottom: "10px", padding: "8px", width: "100%" }} required />
                <input type="file" accept="image/*" onChange={handleImageUpload} style={{ marginBottom: "10px", width: "100%" }} />
                <textarea placeholder="Your Feedback" value={feedbackData.message} onChange={(e) => setFeedbackData({ ...feedbackData, message: e.target.value })} rows="4" style={{ marginBottom: "10px", padding: "8px", width: "100%" }} required />
                <label>Rating (out of 5):</label>
                <input type="number" max={5} min={1} value={feedbackData.rating} onChange={(e) => setFeedbackData({ ...feedbackData, rating: parseInt(e.target.value) })} style={{ marginBottom: "15px", padding: "8px", width: "100%" }} required />
                <button type="submit" className="btn" style={{ backgroundColor: "#5a3531", color: "white", padding: "10px 20px", border: "none", borderRadius: "5px", cursor: "pointer" }}>Submit</button>
              </form>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
