import React, { useEffect, useState } from "react";
import "./UserDashboard.css";

const essentialsKit = {
  dog: [
    "Food & Water Bowls",
    "High-Quality Dog Food",
    "Leash & Collar",
    "Comfortable Bed",
    "Chew Toys & Interactive Toys",
    "Grooming Kit",
    "Poop Bags & Waste Scooper",
    "Training Pads",
    "First Aid Kit",
    "Crate or Carrier",
  ],
  cat: [
    "Food & Water Bowls",
    "High-Quality Cat Food",
    "Litter Box & Litter",
    "Scratching Post",
    "Toys",
    "Comfortable Bed or Blanket",
    "Grooming Kit",
    "Travel Carrier",
    "First Aid Kit",
    "Cat Tree or Climbing Perch",
  ],
};

const UserDashboard = () => {
  const [requests, setRequests] = useState([]);
  const [selectedPet, setSelectedPet] = useState(null);
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    if (!userId) return;

    const fetchRequests = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/adoption-requests/user/${userId}`);
        const data = await res.json();
        setRequests(data);
      } catch (err) {
        console.error("Error fetching requests:", err);
      }
    };

    fetchRequests();
    const interval = setInterval(fetchRequests, 5000);
    return () => clearInterval(interval);
  }, [userId]);

  return (
    <div className="user-dashboard">
      <h2>Your Adoption Requests</h2>
      {requests.length === 0 ? (
        <p>No requests yet.</p>
      ) : (
        <div className="requests-list">
          {requests.map((req) => (
            <div className="request-item" key={req._id}>
              <img
                src={req.petImage || "/images/pets/default.jpeg"}
                className="request-pet-image"
                alt="Pet"
              />
              <div>
                <h3>{req.petName}</h3>
                <p><strong>Reason:</strong> {req.reason}</p>
                <p>
                  <strong>Status:</strong>{" "}
                  <span className={`status ${req.status}`}>{req.status}</span>
                </p>
                {req.status === "approved" && (
                  <p className="success-message">🎉 Congratulations! Your adoption request has been approved.</p>
                )}
                {req.status === "rejected" && (
                  <p className="error-message">❌ Unfortunately, your request was not approved.</p>
                )}
                {req.status === "approved" && (
                  <div className="action-buttons">
                    <button className="btn" onClick={() => setSelectedPet(req)}>Pet Essential Kit</button>
                    <button className="btn">Pet Vaccine Details</button>
                    <button className="btn">Pet Pickup Details</button>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
      
      {/* Modal for Essential Kit */}
      {selectedPet && (
        <div className="modal-overlay" onClick={() => setSelectedPet(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Essential Kit for {selectedPet.petName}</h3>
            
            {/* Debugging: Show pet type */}
            <p><strong>Pet Type:</strong> {selectedPet.petType || "Unknown"}</p>
            
            {/* Display essential kit list */}
            <ul>
              {(essentialsKit[selectedPet.petType?.toLowerCase().trim()] || []).length > 0 ? (
                essentialsKit[selectedPet.petType?.toLowerCase().trim()].map((item, index) => (
                  <li key={index}>{item}</li>
                ))
              ) : (
                <p>No essential kit available for this pet type.</p>
              )}
            </ul>
            <button className="btn close-btn" onClick={() => setSelectedPet(null)}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
