import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Home = () => {
  const testimonials = [
    {
      name: "Aditi & Family",
      message: "Adopting Bruno changed our lives. Thank you, HappyTails!",
      image: "/images/rev2.jpeg",
      rating: 3
    },
    {
      name: "Pooja",
      message: "Mittens is a bundle of love. Best decision ever!",
      image: "/images/rev4.jpeg",
      rating: 4
    },
    {
      name: "Neha & Raj",
      message: "Rusty is the most playful pup. We can't imagine life without him!",
      image: "/images/rev5.jpeg",
      rating: 4
    },
    {
      name: "Akshay",
      message: "Tiny the kitten has brought joy and cuddles into my life.",
      image: "/images/rev3.jpeg",
      rating: 4
    },
    {
      name: "Sarthak",
      message: "Thank you for helping us find our forever companion!",
      image: "/images/rev6.jpeg",
      rating: 3
    },
    {
      name: "Zaveri Family",
      message: "Our little Simba is now the king of our home!",
      image: "/images/rev7.jpeg",
      rating: 3
    },
    {
      name: "Singh Family",
      message: "Adopting through HappyTails was seamless and full of love!",
      image: "/images/rev.jpeg",
      rating: 3
    }
  ];

  const settings = {
    dots: true,
    infinite: true,
    autoplay: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    speed: 1000,
    autoplaySpeed: 2500,
    arrows: false,
    responsive: [
      { breakpoint: 768, settings: { slidesToShow: 1 } },
      { breakpoint: 1024, settings: { slidesToShow: 2 } }
    ]
  };

  return (
    <div>
      {/* 🔹 Hero Section */}
      <div
        className="home-container"
        style={{
          backgroundImage: "url('/images/wall4.png')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          color: "#fff",
          textAlign: "center"
        }}
      >
        <div className="home-content">
          <h1>HappyTails - Adopt, Care & Love</h1>
          <p>Find your perfect furry friend today!</p>
        </div>
      </div>

      {/* 🔸 Testimonial Slider Section */}
      <div
        style={{
          backgroundColor: "#f8f8f8",
          padding: "40px 20px",
          textAlign: "center",
          borderTop: "2px solid #ccc"
        }}
      >
        <h2 style={{ marginBottom: "40px", color: "#5a3531", fontSize: "2rem" }}>
          Tails of Joy 🐾❤️
        </h2>

        <Slider {...settings}>
          {testimonials.map((item, idx) => (
            <div key={idx} style={{ padding: "0 10px" }}>
              <div
                style={{
                  background: "white",
                  borderRadius: "10px",
                  padding: "20px",
                  margin: "0 10px",
                  boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
                  maxWidth: "300px",
                  marginLeft: "auto",
                  marginRight: "auto"
                }}
              >
                <img
                  src={item.image}
                  alt={item.name}
                  style={{ width: "100%", borderRadius: "10px", marginBottom: "10px" }}
                />
                <p style={{ fontStyle: "italic", marginBottom: "10px" }}>
                  “{item.message}”
                </p>
                <strong>{item.name}</strong>
                <div style={{ color: "#ffbb00" }}>
                  {"★".repeat(item.rating) + "☆".repeat(5 - item.rating)}
                </div>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default Home;
