import React, { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const AdoptionRequestForm = ({ petId, petName, onClose }) => {
  const { user } = useContext(AuthContext);

  const [formData, setFormData] = useState({
    petName: petName || "",
    username: "",
    email: "",
    phone: "",
    address: "",
    reason: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const request = {
      ...formData,
      petId,
      userId: user._id
    };

    try {
      const response = await fetch("/api/adoption-requests", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(request)
      });

      if (response.ok) {
        alert("Adoption request submitted successfully!");
        onClose();
      } else {
        alert("Failed to submit request.");
      }
    } catch (error) {
      console.error("Error submitting request:", error);
    }
  };

  return (
    <div style={styles.overlay}>
      <div style={styles.formContainer}>
        <span style={styles.closeBtn} onClick={onClose}>&times;</span>
        <h2>Adoption Request Form</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="petName" value={formData.petName} readOnly style={styles.input} />
          <input type="text" name="username" placeholder="Your Name" value={formData.username} onChange={handleChange} required style={styles.input} />
          <input type="email" name="email" placeholder="Your Email" value={formData.email} onChange={handleChange} required style={styles.input} />
          <input type="text" name="phone" placeholder="Your Phone" value={formData.phone} onChange={handleChange} required style={styles.input} />
          <input type="text" name="address" placeholder="Your Address" value={formData.address} onChange={handleChange} required style={styles.input} />
          <textarea name="reason" placeholder="Why do you want to adopt?" value={formData.reason} onChange={handleChange} required style={{ ...styles.input, height: "80px" }} />
          <button type="submit" style={styles.button}>Submit Request</button>
        </form>
      </div>
    </div>
  );
};

const styles = {
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(0, 0, 0, 0.6)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 1000
  },
  formContainer: {
    background: "white",
    padding: "20px",
    borderRadius: "10px",
    width: "350px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.3)",
    textAlign: "center",
    position: "relative"
  },
  closeBtn: {
    position: "absolute",
    top: "10px",
    right: "15px",
    fontSize: "24px",
    cursor: "pointer"
  },
  input: {
    width: "calc(100% - 20px)",
    padding: "10px",
    margin: "8px 0",
    border: "1px solid #ccc",
    borderRadius: "5px",
    fontSize: "15px"
  },
  button: {
    width: "90%",
    background: "#5a3531",
    color: "white",
    border: "none",
    padding: "10px",
    borderRadius: "5px",
    fontSize: "16px",
    cursor: "pointer"
  }
};

export default AdoptionRequestForm;
