import React, { useState } from "react";
import "./login.css";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [formData, setFormData] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    const res = await fetch("http://localhost:5000/api/users/check", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        email: formData.email,
        password: formData.password,
      }),
    });
  
    if (!res.ok) {
      const errText = await res.text();
      console.error("Login failed:", errText);
      return;
    }
  
    const data = await res.json();
    console.log("✅ Login Success:", data);
  
    // Optional: Redirect after login
    navigate("/home"); // or wherever you'd like to redirect
  };
  

  return (
    <div
      className="login-page"
      style={{
        backgroundImage: `url("/images/wall5.png")`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center"
      }}
    >
      <div className="login-container" style={{ background: "rgba(255, 255, 255, 0.8)", padding: "20px", borderRadius: "10px" }}>
        <h2>{isAdmin ? "Admin Login" : "User Login"}</h2>

        <button className="toggle-btn" onClick={() => setIsAdmin(!isAdmin)}>
          {isAdmin ? "Switch to User Login" : "Switch to Admin Login"}
        </button>

        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Enter Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Enter Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button type="submit">Login</button>
        </form>

        {!isAdmin && (
          <p>
            Don't have an account? <Link to="/register">Register Here</Link>
          </p>
        )}
      </div>
    </div>
  );
};

export default Login;
