import React, { useState, useEffect } from "react";
import AdoptionRequestForm from "./AdoptionRequestForm";

const PetList = () => {
  const [pets, setPets] = useState([]);
  const [selectedPet, setSelectedPet] = useState(null);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetch("/api/pets") // Assuming you have a backend route for fetching pets
      .then((res) => res.json())
      .then((data) => setPets(data))
      .catch((error) => console.error("Error fetching pets:", error));
  }, []);

  const handleAdoptClick = (pet) => {
    setSelectedPet(pet);
    setShowForm(true);
  };

  const handleFormSubmit = async (formData) => {
    const adoptionRequest = {
      ...formData,
      petId: selectedPet._id,
      petName: selectedPet.name,
      status: "Pending",
    };

    try {
      const response = await fetch("/api/adoption-requests", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(adoptionRequest),
      });

      if (response.ok) {
        alert("Adoption request submitted!");
      } else {
        alert("Failed to submit request.");
      }
    } catch (error) {
      console.error("Error submitting request:", error);
    }
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Available Pets</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {pets.map((pet) => (
          <div key={pet._id} className="border p-4 rounded-lg shadow-lg">
            <h2 className="text-lg font-semibold">{pet.name}</h2>
            <p>Breed: {pet.breed}</p>
            <p>Age: {pet.age} years</p>
            <p>Type: {pet.type}</p>
            <button
              onClick={() => handleAdoptClick(pet)}
              className="bg-green-500 text-white px-4 py-2 rounded mt-2"
            >
              Adopt
            </button>
          </div>
        ))}
      </div>

      {showForm && (
        <AdoptionRequestForm
          pet={selectedPet}
          onClose={() => setShowForm(false)}
          onSubmit={handleFormSubmit}
        />
      )}
    </div>
  );
};

export default PetList;
