import React, { useState, useEffect } from "react";
import axios from "axios";
import "./service.css"; // Ensure this file exists

const Services = () => {
  const [selectedService, setSelectedService] = useState(null);
  const [services, setServices] = useState({ Grooming: [], Daycare: [] }); // ✅ Ensure correct structure
  const [centers, setCenters] = useState([]); // Stores service centers

  useEffect(() => {
    axios.get("http://localhost:5000/api/services")
      .then((res) => {
        const groomingServices = res.data.filter(service => service.type.toLowerCase() === "grooming");
        const daycareServices = res.data.filter(service => service.type.toLowerCase() === "daycare");
        setServices({ Grooming: groomingServices, Daycare: daycareServices });
      })
      .catch((err) => console.error("Error fetching services:", err));
  }, []);

  // ✅ Fetch service centers when a service is selected
  useEffect(() => {
    if (selectedService) {
      axios.get(`http://localhost:5000/api/services?type=${selectedService}`)
        .then((res) => setCenters(res.data))
        .catch((err) => console.error("Error fetching centers:", err));
    }
  }, [selectedService]);

  return (
    <div className="services-container">
      <h2>Our Services</h2>

      {/* Service Selection */}
      {!selectedService ? (
        <div className="service-options">
          {/* Predefined Services */}
          <button className="service-btn" onClick={() => setSelectedService("Grooming")}>
            <img src="./images/dg.jpg" alt="Grooming" width="100" />
            <p>Grooming</p>
          </button>
          <button className="service-btn" onClick={() => setSelectedService("Daycare")}>
            <img src="./images/dc.jpg" alt="Daycare" width="100" />
            <p>Daycare</p>
          </button>

          {/* Dynamically Added Services from Admin */}
          {Object.keys(services) // ✅ Corrected
            .filter((service) => service !== "Grooming" && service !== "Daycare")
            .map((service) => (
              <button key={service} className="service-btn" onClick={() => setSelectedService(service)}>
                <p>{service}</p>
              </button>
            ))}
        </div>
      ) : (
        <div>
          <button className="back-btn" onClick={() => setSelectedService(null)}>⬅ Back</button>
          <h3>{selectedService} Centers</h3>
          <ul className="service-list">
            {centers.map((center) => (
              <li key={center._id} className="service-item">
                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                  <h3 style={{ margin: 0 }}>{center.name}</h3>
                  {selectedService.toLowerCase() === "grooming" && (
                    <p style={{ margin: 0 }}>💰 From ₹{center.price || "N/A"}</p>
                  )}
                  {selectedService.toLowerCase() === "daycare" && (
                    <p style={{ margin: 0 }}>💰 ₹{center.price || "N/A"}/hour</p>
                  )}
                </div>
                <p>📍 {center.address}</p>
                <p>📞 {center.contact}</p>

              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Services;
