import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Product.css";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [filterType, setFilterType] = useState("All");
  const [cart, setCart] = useState([]);
  const [showPayment, setShowPayment] = useState(false);
  const [orderPlaced, setOrderPlaced] = useState(false);
  const [customerInfo, setCustomerInfo] = useState({
    name: "",
    address: ""
  });
  const [paymentDetails, setPaymentDetails] = useState({
    method: "",
    name: "",
    cardNumber: "",
    expiry: "",
    cvv: "",
    upi: ""
  });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/products")
      .then((res) => setProducts(res.data))
      .catch((err) => console.error("Error fetching products:", err));

    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const addToCart = (product) => {
    const existingProduct = cart.find((item) => item._id === product._id);

    if (existingProduct) {
      const updatedCart = cart.map((item) =>
        item._id === product._id ? { ...item, quantity: item.quantity + 1 } : item
      );
      setCart(updatedCart);
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
    localStorage.setItem("cart", JSON.stringify(cart));
  };

  const removeFromCart = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleQuantityChange = (index, value) => {
    const updatedCart = [...cart];
    updatedCart[index].quantity = Math.max(1, value);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleBuy = () => {
    if (cart.length === 0) {
      alert("🛒 Your cart is empty!");
      return;
    }
    setShowPayment(true);
  };

  const handlePaymentSubmit = (e) => {
    e.preventDefault();

    if (!customerInfo.name || !customerInfo.address) {
      alert("⚠️ Please enter your name and delivery address.");
      return;
    }

    alert("✅ Payment Successful! Order placed.");
    setCart([]);
    localStorage.removeItem("cart");
    setShowPayment(false);
    setOrderPlaced(true);
    setTimeout(() => setOrderPlaced(false), 5000); // Hide order message after 5s
  };

  const handleChange = (e) => {
    setCustomerInfo({ ...customerInfo, [e.target.name]: e.target.value });
  };

  const handlePaymentChange = (e) => {
    setPaymentDetails({ ...paymentDetails, [e.target.name]: e.target.value });
  };

  const filteredProducts =
    filterType === "All"
      ? products
      : products.filter((product) => product.type === filterType);

  return (
    <>
      {/* NAVBAR */}
      <nav className="navbar2">
        <h2>Pet Products</h2>
        <div className="cart">
          🛒 Cart ({cart.length})
          <div className="cart-dropdown">
            {cart.length === 0 ? (
              <p>Cart is empty</p>
            ) : (
              <>
                <ul>
                  {cart.map((item, index) => (
                    <li key={index}>
                      <img src={item.image} alt={item.name} />
                      <div>
                        <p>{item.name}</p>
                        <p>₹{item.price} x {item.quantity}</p>
                        <input
                          type="number"
                          value={item.quantity}
                          min="1"
                          onChange={(e) => handleQuantityChange(index, parseInt(e.target.value))}
                        />
                        <button onClick={() => removeFromCart(index)}>Remove</button>
                      </div>
                    </li>
                  ))}
                </ul>
                <button className="buy-btn" onClick={handleBuy}>Buy Now</button>
              </>
            )}
          </div>
        </div>
      </nav>

      {/* PRODUCT FILTER */}
      <div className="products-page">
        <div style={{ textAlign: "center", marginBottom: "20px" }}>
          <label style={{ marginRight: "10px", fontWeight: "500" }}>Filter by Type:</label>
          <select value={filterType} onChange={(e) => setFilterType(e.target.value)}>
            <option value="All">All</option>
            {[...new Set(products.map((p) => p.type))].map((type) => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </div>

        {/* PRODUCT LIST */}
        <ul className="product-list">
          {filteredProducts.map((product) => (
            <li key={product._id} className="product-item">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>Type: {product.type}</p>
              <p>Price: ₹{product.price}</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </li>
          ))}
        </ul>
      </div>

      {/* PAYMENT MODAL */}
      {showPayment && (
        <div className="payment-modal">
          <div className="payment-content">
            <h2>Payment Gateway</h2>

            {/* Customer Info */}
            <form onSubmit={handlePaymentSubmit}>
              <h3>Delivery Details</h3>
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={customerInfo.name}
                onChange={handleChange}
                required
              />
              <textarea
                name="address"
                placeholder="Delivery Address"
                value={customerInfo.address}
                onChange={handleChange}
                required
              />

              {/* Payment Method */}
              <h3>Payment Method</h3>
              <label><input type="radio" name="method" value="card" onChange={handlePaymentChange} /> Card</label>
              <label><input type="radio" name="method" value="upi" onChange={handlePaymentChange} /> UPI</label>
              <label><input type="radio" name="method" value="cod" onChange={handlePaymentChange} /> Cash on Delivery</label>

              {paymentDetails.method === "card" && (
                <>
                  <input type="text" name="cardNumber" placeholder="Card Number" onChange={handlePaymentChange} required />
                  <input type="text" name="expiry" placeholder="MM/YY" onChange={handlePaymentChange} required />
                  <input type="text" name="cvv" placeholder="CVV" onChange={handlePaymentChange} required />
                </>
              )}
              {paymentDetails.method === "upi" && (
                <input type="text" name="upi" placeholder="UPI ID" onChange={handlePaymentChange} required />
              )}

              <button type="submit">Proceed</button>
              <button type="button" onClick={() => setShowPayment(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}

      {/* ORDER CONFIRMATION */}
      {orderPlaced && (
        <div className="order-success">
          <h2>🎉 Order Placed Successfully!</h2>
          <p>🚚 Your order will be delivered within **2 days**.</p>
        </div>
      )}
    </>
  );
};

export default Products;
