import React, { useState } from "react";

const AdoptionRequestForm = ({ pet, onClose }) => {
  const [formData, setFormData] = useState({
    petName: pet?.name || "",
    username: "",
    email: "",
    phone: "",
    address: "",
    reason: ""
  });

  // ✅ Prevent rendering if no pet is passed
  if (!pet) return null;

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      ...formData,
      petId: pet._id
    };

    try {
      const res = await fetch("http://localhost:5000/api/adoption-requests", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });

      if (res.ok) {
        alert("Adoption request submitted successfully!");
        onClose(); // Close the modal
      } else {
        alert("Failed to submit adoption request.");
      }
    } catch (err) {
      console.error("Error submitting adoption request:", err);
      alert("Something went wrong!");
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-96">
        <h2 className="text-xl font-bold mb-4">
          Adoption Request for <span className="text-blue-700">{pet.name}</span>
        </h2>
        <form onSubmit={handleSubmit}>
          {/* Read-only Pet Name */}
          <div className="mb-2">
            <label className="block text-sm font-medium">Pet Name</label>
            <input
              type="text"
              name="petName"
              value={formData.petName}
              readOnly
              className="w-full border p-2 rounded bg-gray-100 text-gray-600"
            />
          </div>

          <div className="mb-2">
            <label className="block text-sm font-medium">Your Name</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              className="w-full border p-2 rounded"
              required
              onChange={handleChange}
            />
          </div>

          <div className="mb-2">
            <label className="block text-sm font-medium">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              className="w-full border p-2 rounded"
              required
              onChange={handleChange}
            />
          </div>

          <div className="mb-2">
            <label className="block text-sm font-medium">Phone</label>
            <input
              type="text"
              name="phone"
              value={formData.phone}
              className="w-full border p-2 rounded"
              required
              onChange={handleChange}
            />
          </div>

          <div className="mb-2">
            <label className="block text-sm font-medium">Address</label>
            <textarea
              name="address"
              value={formData.address}
              className="w-full border p-2 rounded"
              required
              onChange={handleChange}
            ></textarea>
          </div>

          <div className="mb-2">
            <label className="block text-sm font-medium">Reason for Adoption</label>
            <textarea
              name="reason"
              value={formData.reason}
              className="w-full border p-2 rounded"
              required
              onChange={handleChange}
            ></textarea>
          </div>

          <div className="flex justify-end mt-4">
            <button
              type="button"
              onClick={onClose}
              className="bg-gray-500 text-white px-4 py-2 rounded mr-2"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded"
            >
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdoptionRequestForm;
