import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Product.css";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [filterType, setFilterType] = useState("All");
  const [cart, setCart] = useState([]);
  const [showPayment, setShowPayment] = useState(false);
  const [paymentDetails, setPaymentDetails] = useState({
    name: "",
    cardNumber: "",
    expiry: "",
    cvv: "",
    upi: ""
  });

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/products")
      .then((res) => setProducts(res.data))
      .catch((err) => console.error("Error fetching products:", err));

    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const addToCart = (product) => {
    const updatedCart = [...cart, product];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const removeFromCart = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleBuy = () => {
    setShowPayment(true);
  };

  const handlePaymentSubmit = (e) => {
    e.preventDefault();
    alert("âœ… Payment Successful! Order placed.");
    setCart([]);
    localStorage.removeItem("cart");
    setShowPayment(false);
    setPaymentDetails({
      name: "",
      cardNumber: "",
      expiry: "",
      cvv: "",
      upi: ""
    });
  };

  const handlePaymentChange = (e) => {
    setPaymentDetails({ ...paymentDetails, [e.target.name]: e.target.value });
  };

  const filteredProducts =
    filterType === "All"
      ? products
      : products.filter((product) => product.type === filterType);

  return (
    <>
      {/* NAVBAR */}
      <nav className="navbar2">
        <h2>Pet Products</h2>
        // eslint-disable-next-line
        <div className="cart">
          ðŸ›’ Cart ({cart.length})
          <div className="cart-dropdown">
            {cart.length === 0 ? (
              <p>Cart is empty</p>
            ) : (
              <>
                <ul>
                  {cart.map((item, index) => (
                    <li key={index}>
                      <img src={item.image} alt={item.name} />
                      <div>
                        <p>{item.name}</p>
                        <p>â‚¹{item.price}</p>
                        <button onClick={() => removeFromCart(index)}>Remove</button>
                      </div>
                    </li>
                  ))}
                </ul>
                <button className="buy-btn" onClick={handleBuy}>Buy Now</button>
              </>
            )}
          </div>
        </div>
      </nav>

      {/* PRODUCT FILTER */}
      <div className="products-page">
        <div style={{ textAlign: "center", marginBottom: "20px" }}>
          <label style={{ marginRight: "10px", fontWeight: "500" }}>Filter by Type:</label>
          <select value={filterType} onChange={(e) => setFilterType(e.target.value)}>
            <option value="All">All</option>
            {[...new Set(products.map((p) => p.type))].map((type) => (
              <option key={type} value={type}>{type}</option>
            ))}
          </select>
        </div>

        {/* PRODUCT LIST */}
        <ul className="product-list">
          {filteredProducts.map((product) => (
            <li key={product._id} className="product-item">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>Type: {product.type}</p>
              <p>Price: â‚¹{product.price}</p>
              <button onClick={() => addToCart(product)}>Add to Cart</button>
            </li>
          ))}
        </ul>
      </div>

      {/* PAYMENT MODAL */}
      {showPayment && (
        <div className="payment-modal">
          <div className="payment-content">
            <img
              alt="Payment Gateway"
              style={{ width: "130px", margin: "0 auto 20px", display: "block" }}
            />
            <p style={{ fontSize: "16px", marginBottom: "15px" }}>
              Paying â‚¹<strong>{cart.reduce((acc, item) => acc + item.price, 0)}</strong> for <strong>{cart.length}</strong> item(s)
            </p>

            <form onSubmit={handlePaymentSubmit}>
              <input type="text" name="name" placeholder="Cardholder Name" value={paymentDetails.name} onChange={handlePaymentChange} required />
              <input type="text" name="cardNumber" placeholder="ðŸ’³ Card Number" value={paymentDetails.cardNumber} onChange={handlePaymentChange} required />
              <div style={{ display: "flex", gap: "10px" }}>
                <input type="text" name="expiry" placeholder="MM/YY" value={paymentDetails.expiry} onChange={handlePaymentChange} required style={{ flex: 1 }} />
                <input type="text" name="cvv" placeholder="CVV" value={paymentDetails.cvv} onChange={handlePaymentChange} required style={{ flex: 1 }} />
              </div>
              <input type="text" name="upi" placeholder="ðŸ¦ UPI ID (optional)" value={paymentDetails.upi} onChange={handlePaymentChange} />

              <button type="submit">Pay Now</button>
              <button type="button" onClick={() => setShowPayment(false)}>Cancel</button>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default Products;
