import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg"; // Adjust the path as needed

function Navbar() {
  const navigate = useNavigate();

  // State to track login status and user type
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem("isLoggedIn") === "true");
  const [userType, setUserType] = useState(localStorage.getItem("userType")?.toLowerCase());

  // Listen for localStorage changes to update state
  useEffect(() => {
    const updateNavbar = () => {
      setIsLoggedIn(localStorage.getItem("isLoggedIn") === "true");
      setUserType(localStorage.getItem("userType")?.toLowerCase());
    };

    window.addEventListener("storage", updateNavbar);
    return () => window.removeEventListener("storage", updateNavbar);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("userType");
    setIsLoggedIn(false);
    setUserType(null);
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-logo">
        <img src={logo} alt="Happy Tails Logo" className="logo-img" />
        <h1>Happy Tails</h1>
      </Link>

      <ul>
        <li><Link to="/">Home</Link></li>

        {isLoggedIn ? (
          <>
            <li><Link to="/petlist">Pets</Link></li>
            <li><Link to="/service">Services</Link></li>
            <li><Link to="/product">Products</Link></li>

            {userType === "admin" && <li><Link to="/admin-dashboard">Admin Dashboard</Link></li>}
            {userType === "user" && <li><Link to="/user-dashboard">User Dashboard</Link></li>}

            <li><Link to="/login" onClick={handleLogout}>Logout</Link></li>
          </>
        ) : (
          <li><Link to="/login">Login</Link></li>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
