import React from "react";

const Home = () => {
  return (
    <div>
      {/* üîπ Hero Section */}
      <div
        className="home-container"
        style={{
          backgroundImage: "url('/images/wall4.png')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          color: "#fff",
          textAlign: "center"
        }}
      >
        <div className="home-content">
          <h1>HappyTails - Adopt, Care & Love</h1>
          <p>Find your perfect furry friend today!</p>
        </div>
      </div>

      {/* üî∏ Dummy Happy Customers Section (Footer) */}
      <div
        style={{
          backgroundColor: "#f8f8f8",
          padding: "40px 20px",
          textAlign: "center",
          borderTop: "2px solid #ccc"
        }}
      >
        <h2 style={{ marginBottom: "20px", color: "#5a3531" }}>Meet our adopters</h2>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "40px",
            flexWrap: "wrap"
          }}
        >
          <div style={{ maxWidth: "250px" }}>
            <img
              src="/images/rev2.jpeg"
              alt="Happy Dog"
              style={{ width: "100%", borderRadius: "10px", marginBottom: "10px" }}
            />
            <p>‚ÄúAdopting Bruno changed our lives. Thank you, HappyTails!‚Äù</p>
            <strong>- Aditi & Family</strong>
          </div>

          <div style={{ maxWidth: "250px" }}>
            <img
              src="/images/rev4.jpeg"
              alt="Happy Cat"
              style={{ width: "100%", borderRadius: "10px", marginBottom: "10px" }}
            />
            <p>‚ÄúMittens is a bundle of love. Best decision ever!‚Äù</p>
            <strong>- Rohan & Pooja</strong>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
