import React, { useState } from "react";
import "./login.css";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [formData, setFormData] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (isAdmin) {
      // ✅ Admin Login (Hardcoded)
      if (formData.email === "admin@gmail.com" && formData.password === "Admin@123") {
        alert("✅ Admin Logged In Successfully!");
        localStorage.setItem("isLoggedIn", "true");
        localStorage.setItem("userType", "admin");
        localStorage.setItem("userId", "admin123");
        navigate("/admin-dashboard");
      } else {
        alert("❌ Invalid Admin Credentials!");
      }
    } else {
      // ✅ Check if user exists in MongoDB
      try {
        const response = await fetch("http://localhost:5000/api/users/check", { 
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email: formData.email }),
        });
        
        const data = await response.json();

        if (data.exists) {
          // ✅ Proceed with login
          alert("✅ User exists, logging in...");
          localStorage.setItem("isLoggedIn", "true");
          localStorage.setItem("userType", "user");
          localStorage.setItem("userId", formData.email);
          navigate("/pets"); // ✅ Redirect to Pets page
        } else {
          // ❌ User does not exist, redirect to register
          alert("❌ User not found! Please register first.");
          navigate("/register");
        }
      } catch (error) {
        console.error("Login error:", error);
        alert("❌ Something went wrong! Try again.");
      }
    }
  }

  return (
    <div
      className="login-page"
      style={{
        backgroundImage: `url("/images/wall5.png")`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        height: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center"
      }}
    >
      <div className="login-container" style={{ background: "rgba(255, 255, 255, 0.8)", padding: "20px", borderRadius: "10px" }}>
        <h2>{isAdmin ? "Admin Login" : "User Login"}</h2>

        <button className="toggle-btn" onClick={() => setIsAdmin(!isAdmin)}>
          {isAdmin ? "Switch to User Login" : "Switch to Admin Login"}
        </button>

        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Enter Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Enter Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button type="submit">Login</button>
        </form>

        {!isAdmin && (
          <p>
            Don't have an account? <Link to="/register">Register Here</Link>
          </p>
        )}
      </div>
    </div>
  );
};

export default Login;
