import React, { useState, useEffect } from "react";
import axios from "axios";

const PetListings = () => {
  const [pets, setPets] = useState([]);
  const [newPet, setNewPet] = useState({
    name: "",
    age: "",
    breed: "",
    type: "Dog",
    gender: "Male",
    image: "",
  });

  const fetchPets = () => {
    axios
      .get("http://localhost:5000/api/pets")
      .then((response) => {
        setPets(response.data);
        localStorage.setItem("pets", JSON.stringify(response.data));
      })
      .catch((error) => console.error("Error fetching pets:", error));
  };

  useEffect(() => {
    fetchPets();
  }, []);

  const handleAddPet = async () => {
    if (
      !newPet.name ||
      !newPet.age ||
      !newPet.breed ||
      !newPet.type ||
      !newPet.gender ||
      !newPet.image
    ) {
      alert("All fields are required!");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/pets", newPet, {
        headers: { "Content-Type": "application/json" },
      });
      fetchPets();
      setNewPet({
        name: "",
        age: "",
        breed: "",
        type: "Dog",
        gender: "Male",
        image: "",
      });
    } catch (error) {
      console.error("Error adding pet:", error);
    }
  };

  const handleDeletePet = async (petId) => {
    try {
      await axios.delete(`http://localhost:5000/api/pets/${petId}`);
      fetchPets();
    } catch (error) {
      console.error("Error deleting pet:", error);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewPet((prevPet) => ({ ...prevPet, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div>
      <h2>Manage Pet Listings</h2>
      <div className="product-form">
        <select
          value={newPet.type}
          onChange={(e) =>
            setNewPet({ ...newPet, type: e.target.value })
          }
        >
          <option value="Dog">Dog</option>
          <option value="Cat">Cat</option>
        </select>
        <input
          type="text"
          placeholder="Pet Name"
          value={newPet.name}
          onChange={(e) =>
            setNewPet({ ...newPet, name: e.target.value })
          }
        />
        <input
          type="number"
          placeholder="Age (months)"
          value={newPet.age}
          onChange={(e) =>
            setNewPet({ ...newPet, age: e.target.value })
          }
        />
        <input
          type="text"
          placeholder="Breed"
          value={newPet.breed}
          onChange={(e) =>
            setNewPet({ ...newPet, breed: e.target.value })
          }
        />

        {/* âœ… Radio buttons for gender */}
        <div style={{ display: "flex", gap: "10px", alignItems: "center" }}>
          <label><strong>Gender:</strong></label>
          <label>
            <input
              type="radio"
              name="gender"
              value="Male"
              checked={newPet.gender === "Male"}
              onChange={(e) =>
                setNewPet({ ...newPet, gender: e.target.value })
              }
            />
            Male
          </label>
          <label>
            <input
              type="radio"
              name="gender"
              value="Female"
              checked={newPet.gender === "Female"}
              onChange={(e) =>
                setNewPet({ ...newPet, gender: e.target.value })
              }
            />
            Female
          </label>
        </div>

        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
        />
        <button onClick={handleAddPet}>Add Pet</button>
      </div>

      <ul className="product-list">
        {pets.map((pet) => (
          <li key={pet._id} className="product-item">
            <p>Type: {pet.type}</p>
            <img src={pet.image} alt={pet.name} width="100" />
            <h3>{pet.name}</h3>
            <p>Age: {pet.age} months</p>
            <p>Gender: {pet.gender}</p>
            <p>Breed: {pet.breed}</p>
            <button onClick={() => handleDeletePet(pet._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PetListings;
