import React, { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const AdoptionRequestForm = ({ pet, onClose }) => {
  // ✅ Always call hooks first
  const [formData, setFormData] = useState({
    petName: "",
    username: "",
    email: "",
    phone: "",
    address: "",
    reason: ""
  });

  // ✅ Update petName once pet is available
  useEffect(() => {
    if (pet) {
      setFormData((prev) => ({
        ...prev,
        petName: pet.name
      }));
    }
  }, [pet]);

  // ✅ Exit early if no pet
  if (!pet) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      ...formData,
      petId: pet._id
    };

    try {
      const res = await fetch("http://localhost:5000/api/adoption-requests", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });

      if (res.ok) {
        toast.success("✅ Adoption request submitted!");
        onClose();
      } else {
        toast.error("❌ Failed to submit request.");
      }
    } catch (error) {
      console.error("Error:", error);
      toast.error("❌ Server error!");
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-96">
        <h2 className="text-xl font-bold mb-4">
          Adoption Request for{" "}
          <span className="text-blue-700">{pet.name}</span>
        </h2>

        <form onSubmit={handleSubmit}>
          {/* Pet Name */}
          <div className="mb-2">
            <label className="block text-sm font-medium">Pet Name</label>
            <input
              type="text"
              name="petName"
              value={formData.petName}
              readOnly
              className="w-full border p-2 rounded bg-gray-100 text-gray-600"
            />
          </div>

          {/* Username */}
          <div className="mb-2">
            <label className="block text-sm font-medium">Your Name</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            />
          </div>

          {/* Email */}
          <div className="mb-2">
            <label className="block text-sm font-medium">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            />
          </div>

          {/* Phone */}
          <div className="mb-2">
            <label className="block text-sm font-medium">Phone</label>
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            />
          </div>

          {/* Address */}
          <div className="mb-2">
            <label className="block text-sm font-medium">Address</label>
            <textarea
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            ></textarea>
          </div>

          {/* Reason */}
          <div className="mb-2">
            <label className="block text-sm font-medium">
              Reason for Adoption
            </label>
            <textarea
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
            ></textarea>
          </div>

          {/* Buttons */}
          <div className="flex justify-end mt-4">
            <button
              type="button"
              onClick={onClose}
              className="bg-gray-500 text-white px-4 py-2 rounded mr-2"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded"
            >
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdoptionRequestForm;
