import React, { useEffect, useState } from "react";

const UserDashboard = () => {
  const [requests, setRequests] = useState([]);
  const userId = localStorage.getItem("userId"); 

  useEffect(() => {
    if (!userId) return;

    const fetchRequests = () => {
      fetch(`http://localhost:5000/api/adoption-requests/user/${userId}`)
        .then((res) => res.json())
        .then((data) => setRequests(data))
        .catch((err) => console.error("Error fetching requests:", err));
    };

    fetchRequests(); // Initial call

    const interval = setInterval(fetchRequests, 5000); // Polling every 5 sec

    return () => clearInterval(interval); // Cleanup on unmount
  }, [userId]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Your Adoption Requests</h2>
  
      {requests.length === 0 ? (
        <p>No requests yet.</p>
      ) : (
        <div style={{ display: "grid", gap: "15px", marginTop: "20px" }}>
          {requests.map((req) => (
            <div
              key={req._id}
              style={{
                display: "flex",
                alignItems: "flex-start",
                gap: "15px",
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "6px",
                background: "#fff"
              }}
            >
              <div>
                <h3 style={{ margin: "0 0 5px" }}>{req.petName}</h3>
                <p style={{ margin: "4px 0" }}>
                  <strong>Reason:</strong> {req.reason}
                </p>
                <p style={{ margin: "4px 0" }}>
                  <strong>Status:</strong>{" "}
                  <span
                    style={{
                      color:
                        req.status === "approved, APPROVED"
                          ? "green"
                          : req.status === "rejected"
                          ? "red"
                          : "#888",
                      fontWeight: "bold"
                    }}
                  >
                    {req.status}
                  </span>
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
  
};

export default UserDashboard;
