import React from "react";
import { Link, useNavigate } from "react-router-dom";
import logo from "../assets/logo.jpg"; // Adjust the path as needed

function Navbar() {
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem("isLoggedIn");
  const userType = localStorage.getItem("userType")?.toLowerCase(); // Ensure lowercase for consistency

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("userType");
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-logo">
        <img src={logo} alt="Happy Tails Logo" className="logo-img" />
        <h1>Happy Tails</h1>
      </Link>

      <ul>
        <li><Link to="/">Home</Link></li>

        {isLoggedIn ? (
          <>
            <li><Link to="/petlist">Pets</Link></li>
            <li><Link to="/service">Services</Link></li>
            <li><Link to="/product">Products</Link></li>

            {/* Show correct dashboard based on userType */}
            {userType === "admin" && (
              <li><Link to="/admin-dashboard">Admin Dashboard</Link></li>
            )}
            {userType === "user" && (
              <li><Link to="/user-dashboard">User Dashboard</Link></li>
            )}

            <li><Link to="/" onClick={handleLogout}>Logout</Link></li>
          </>
        ) : (
          <li><Link to="/login">Login</Link></li>
        )}
      </ul>

    </nav>
  );
}

export default Navbar;
