import React, { useEffect, useState } from "react";

const UserDashboard = () => {
  const [requests, setRequests] = useState([]);

  const userId = localStorage.getItem("userId");

  useEffect(() => {
    if (!userId) return;

    fetch(`http://localhost:5000/api/adoption-requests/user/${userId}`)
      .then((res) => res.json())
      .then((data) => setRequests(data))
      .catch((err) => console.error("Error fetching requests:", err));
  }, [userId]);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Your Adoption Requests</h2>

      {requests.length === 0 ? (
        <p>No requests yet.</p>
      ) : (
        <div style={{ display: "grid", gap: "15px", marginTop: "20px" }}>
          {requests.map((req) => (
            <div key={req._id} style={{ border: "1px solid #ccc", padding: "10px", borderRadius: "6px" }}>
              <h3>üêæ {req.petName}</h3>
              <p><strong>Reason:</strong> {req.reason}</p>
              <p>
                <strong>Status:</strong>{" "}
                <span style={{
                  color:
                    req.status === "approved" ? "green" :
                    req.status === "rejected" ? "red" : "#888"
                }}>
                  {req.status}
                </span>
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
