import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import AddPetForm from "./addPet";
import AdoptionRequestForm from "./adoptionForm"; // âœ… Import form
import "./petList.css";

function PetList() {
  const [pets, setPets] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [selectedPet, setSelectedPet] = useState(null);
  const navigate = useNavigate();

  const fetchPets = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/pets");
      setPets(response.data);
    } catch (error) {
      console.error("Error fetching pets:", error);
    }
  };

  useEffect(() => {
    fetchPets();
  }, []);

  const handleAdoptClick = (pet) => {
    setSelectedPet(pet);
    setShowForm(true);
  };

  return (
    <div className="pets-page">
      <AddPetForm onPetAdded={fetchPets} />

      <div className="pet-list-container">
        <h2>Available Pets for Adoption</h2>
        <div className="pet-container">
          {pets.length > 0 ? (
            pets.map((pet) => (
              <div key={pet._id} className="pet-card">
                <img
                  src={pet.image ? pet.image : "/default-pet.jpg"}
                  alt={pet.name || "Unnamed Pet"}
                  className="pet-image"
                />
                <h3>{pet.name || "Unnamed Pet"}</h3>
                <p><strong>Breed:</strong> {pet.breed || "Unknown"}</p>
                <p><strong>Age:</strong> {pet.age ? `${pet.age} years` : "Unknown"}</p>
                <button onClick={() => handleAdoptClick(pet)}>Adopt</button>
              </div>
            ))
          ) : (
            <p>No pets available.</p>
          )}
        </div>
      </div>

      {/* ðŸ”¹ Show modal if a pet is selected */}
      {showForm && (
        <AdoptionRequestForm
          pet={selectedPet}
          onClose={() => setShowForm(false)}
          onSubmit={handleFormSubmit}
        />
      )}
    </div>
  );
};

export default PetList;
