import React, { useState } from "react";

const AdoptionRequestForm = ({ pet, onClose, onSubmit }) => {
  if (!pet) return null; // 🛡️ Guard against undefined

  const [formData, setFormData] = useState({
    petName: pet.name || "",
    username: "",
    email: "",
    phone: "",
    address: "",
    reason: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    onClose();
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-96">
        <h2 className="text-xl font-bold mb-4">Adoption Request for <span className="text-blue-700">{pet.name}</span></h2>
        <form onSubmit={handleSubmit}>
          
          {/* 🔹 Pet Name Field (Read-only) */}
          <div className="mb-2">
            <label className="block text-sm font-medium">Pet Name</label>
            <input
              type="text"
              name="petName"
              value={pet.name}
              readOnly
              className="w-full border p-2 rounded bg-gray-100 text-gray-600"
            />
          </div>
  
          <div className="mb-2">
            <label className="block text-sm font-medium">Name</label>
            <input type="text" name="name" className="w-full border p-2 rounded" required onChange={handleChange} />
          </div>
          <div className="mb-2">
            <label className="block text-sm font-medium">Email</label>
            <input type="email" name="email" className="w-full border p-2 rounded" required onChange={handleChange} />
          </div>
          <div className="mb-2">
            <label className="block text-sm font-medium">Phone</label>
            <input type="text" name="phone" className="w-full border p-2 rounded" required onChange={handleChange} />
          </div>
          <div className="mb-2">
            <label className="block text-sm font-medium">Address</label>
            <textarea name="address" className="w-full border p-2 rounded" required onChange={handleChange}></textarea>
          </div>
          <div className="mb-2">
            <label className="block text-sm font-medium">Reason for Adoption</label>
            <textarea name="reason" className="w-full border p-2 rounded" required onChange={handleChange}></textarea>
          </div>
  
          <div className="flex justify-end mt-4">
            <button type="button" onClick={onClose} className="bg-gray-500 text-white px-4 py-2 rounded mr-2">Cancel</button>
            <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Submit</button>
          </div>
        </form>
      </div>
    </div>
  );

};

export default AdoptionRequestForm;
