import React, { useState, useEffect } from "react";

const AdoptionRequestForm = ({ pet, onClose }) => {
  const [formData, setFormData] = useState({
    petName: "",
    username: "",
    email: "",
    phone: "",
    address: "",
    reason: ""
  });

  // Pre-fill pet name when modal opens
  useEffect(() => {
    if (pet) {
      setFormData((prev) => ({
        ...prev,
        petName: pet.name || ""
      }));
    }
  }, [pet]);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/api/adoption-requests", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...formData, petId: pet._id })
      });

      if (res.ok) {
        alert("✅ Adoption request submitted!");
        onClose();
      } else {
        alert("❌ Failed to submit request.");
      }
    } catch (error) {
      console.error("Submission error:", error);
      alert("❌ Server error. Please try again later.");
    }
  };

  if (!pet) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center">
      {/* Prevent background scroll while modal is open */}
      <div className="fixed inset-0 overflow-hidden"></div>

      {/* Modal Content */}
      <div className="relative bg-white w-full max-w-md mx-auto p-6 rounded-lg shadow-lg z-50">
        {/* Close Button */}
        <button
          onClick={onClose}
          className="absolute top-3 right-4 text-2xl text-gray-500 hover:text-black"
        >
          &times;
        </button>

        <h2 className="text-xl font-bold mb-4 text-center">
          Adoption Request for <span className="text-blue-700">{pet.name}</span>
        </h2>

        <form onSubmit={handleSubmit} className="space-y-3">
          {/* Pet Name (read-only) */}
          <div>
            <label className="block text-sm font-medium">Pet Name</label>
            <input
              type="text"
              name="petName"
              value={formData.petName}
              readOnly
              className="w-full border p-2 rounded bg-gray-100 text-gray-600"
            />
          </div>

          <div>
            <label className="block text-sm font-medium">Your Name</label>
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
              placeholder="Enter your name"
            />
          </div>

          <div>
            <label className="block text-sm font-medium">Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
              placeholder="Enter your email"
            />
          </div>

          <div>
            <label className="block text-sm font-medium">Phone</label>
            <input
              type="text"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
              placeholder="Enter phone number"
            />
          </div>

          <div>
            <label className="block text-sm font-medium">Address</label>
            <textarea
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
              placeholder="Enter your address"
            />
          </div>

          <div>
            <label className="block text-sm font-medium">Reason for Adoption</label>
            <textarea
              name="reason"
              value={formData.reason}
              onChange={handleChange}
              required
              className="w-full border p-2 rounded"
              placeholder="Why do you want to adopt?"
            />
          </div>

          <div className="flex justify-end space-x-2 pt-3">
            <button
              type="button"
              onClick={onClose}
              className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AdoptionRequestForm;
