import React, { useState, useEffect } from "react";
import axios from "axios";
import "./service.css"; // Make sure this exists

const Services = () => {
  const [selectedService, setSelectedService] = useState(null);
  const [services, setServices] = useState({ Grooming: [], Daycare: [] });
  const [centers, setCenters] = useState([]);
  const [visibleContactId, setVisibleContactId] = useState(null); // 👈 for showing contact

  useEffect(() => {
    axios.get("http://localhost:5000/api/services")
      .then((res) => {
        const grooming = res.data.filter(service => service.type.toLowerCase() === "grooming");
        const daycare = res.data.filter(service => service.type.toLowerCase() === "daycare");
        setServices({ Grooming: grooming, Daycare: daycare });
      })
      .catch((err) => console.error("Error fetching services:", err));
  }, []);

  useEffect(() => {
    if (selectedService) {
      axios.get(`http://localhost:5000/api/services?type=${selectedService}`)
        .then((res) => setCenters(res.data))
        .catch((err) => console.error("Error fetching centers:", err));
    }
  }, [selectedService]);

  return (
    <div className="services-container">
      <h2>Our Services</h2>

      {/* Service Selection */}
      {!selectedService ? (
        <div className="service-options">
          <button className="service-btn" onClick={() => setSelectedService("Grooming")}>
            <img src="./images/dg.jpg" alt="Grooming" width="100" />
            <p>Grooming</p>
          </button>
          <button className="service-btn" onClick={() => setSelectedService("Daycare")}>
            <img src="./images/dc.jpg" alt="Daycare" width="100" />
            <p>Daycare</p>
          </button>
          {Object.keys(services)
            .filter((service) => service !== "Grooming" && service !== "Daycare")
            .map((service) => (
              <button key={service} className="service-btn" onClick={() => setSelectedService(service)}>
                <p>{service}</p>
              </button>
            ))}
        </div>
      ) : (
        <div>
          <button className="back-btn" onClick={() => setSelectedService(null)}>⬅ Back</button>
          <h3>{selectedService} Centers</h3>
          <ul className="service-list">
            {centers.map((center) => (
              <li
                key={center._id}
                className="service-item"
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  padding: "20px",
                  borderRadius: "10px",
                  boxShadow: "0 4px 10px rgba(0,0,0,0.05)",
                  marginBottom: "15px",
                  background: "#f9f9f9",
                }}
              >
                {/* Center Details - Center Aligned */}
                <div style={{ flex: 1 }}>
                  <div style={{ textAlign: "center", marginBottom: "10px" }}>
                    <h3 style={{ margin: 0 }}>{center.name}</h3>
                    <p style={{ margin: "5px 0" }}>📍 {center.address}</p>
                  </div>
                </div>

                {/* Price + Call button - Right Aligned */}
                <div style={{ textAlign: "right" }}>
                  {selectedService.toLowerCase() === "grooming" && (
                    <p style={{ margin: 0, fontWeight: "bold" }}>💰 From ₹{center.price || "N/A"}</p>
                  )}
                  {selectedService.toLowerCase() === "daycare" && (
                    <p style={{ margin: 0, fontWeight: "bold" }}>💰 ₹{center.price || "N/A"}/hour</p>
                  )}

                  <button
                    style={{
                      marginTop: "8px",
                      padding: "6px 12px",
                      backgroundColor: "#5a3531",
                      color: "white",
                      border: "none",
                      borderRadius: "6px",
                      cursor: "pointer",
                    }}
                    onClick={() =>
                      setVisibleContactId(prev =>
                        prev === center._id ? null : center._id
                      )
                    }
                  >
                    📞 Call Now
                  </button>

                  {visibleContactId === center._id && (
                    <p style={{ marginTop: "6px", color: "#333" }}>
                      Call on this number: <strong>{center.contact}</strong>
                    </p>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Services;
