import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";


const AdoptionRequestForm = () => {
  const { petId } = useParams();  // Get petId from URL
  const navigate = useNavigate();  // Used to navigate back
  const location = useLocation();  // Get pet name from state (if passed)
  const { user } = useContext(AuthContext); // ✅ Access logged-in user

  // Get petName from location.state (default to empty string if undefined)
  const petNameFromState = location.state?.petName || "";

  const [formData, setFormData] = useState({
    petname: petNameFromState,
    username: "",
    email: "",
    phone: "",
    address: "",
    reason: ""
  });

   // Set pet name automatically from state or fallback to an empty string
   useEffect(() => {
    setFormData((prev) => ({ ...prev, petName: petNameFromState }));
  }, [petNameFromState]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const request = {
      ...formData,
      petId,
      userId: user._id, 
    };

    try {
      const response = await fetch("/api/adoption-requests", { 
        method: "POST", 
        headers: { "Content-Type": "application/json" }, 
        body: JSON.stringify(request) 
      });      

      if (response.ok) {
        alert("Adoption request submitted successfully!");
        navigate(-1); // Go back after successful submission
      } else {
        alert("Failed to submit request.");
      }
    } catch (error) {
      console.error("Error submitting request:", error);
    }
  };

  return (
    <div style={{
      position: "fixed",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      background: "rgba(0, 0, 0, 0.5)",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      zIndex: 1000
    }}>
      <div style={{
        background: "white",
        padding: "20px",
        borderRadius: "10px",
        width: "350px",
        boxShadow: "0 4px 10px rgba(0, 0, 0, 0.3)",
        textAlign: "center",
        position: "relative"
      }}>
        <span style={{
          position: "absolute",
          top: "15px",
          right: "20px",
          fontSize: "24px",
          cursor: "pointer"
        }} onClick={() => navigate(-1)}>&times;</span>

        <h2>Adoption Request Form</h2>
        <form onSubmit={handleSubmit}>
        <input type="text" name="petName" value={formData.petname} readOnly
            style={{
              width: "calc(100% - 20px)",
              maxWidth: "360px",
              padding: "5px",
              margin: "5px",
              border: "1px solid #ccc",
              borderRadius: "5px",
              fontSize: "16px",
              display: "block",
              boxSizing: "border-box"
            }} 
          />
          <input type="text" name="username" placeholder="Your Name" value={formData.username} onChange={handleChange} required
            style={{
              width: "calc(100% - 20px)",
              maxWidth: "360px",
              padding: "5px",
              margin: "5px",
              border: "1px solid #ccc",
              borderRadius: "5px",
              fontSize: "16px",
              display: "block",
              boxSizing: "border-box"
            }} 
          />
          <input type="email" name="email" placeholder="Your Email" value={formData.email} onChange={handleChange} required 
            style={{
              width: "calc(100% - 20px)",
              maxWidth: "360px",
              padding: "5px",
              margin: "5px",
              border: "1px solid #ccc",
              borderRadius: "5px",
              fontSize: "16px",
              display: "block",
              boxSizing: "border-box"
            }} 
          />
          <input type="text" name="phone" placeholder="Your Phone" value={formData.phone} onChange={handleChange} required 
            style={{
              width: "calc(100% - 20px)",
              maxWidth: "360px",
              padding: "5px",
              margin: "5px",
              border: "1px solid #ccc",
              borderRadius: "5px",
              fontSize: "16px",
              display: "block",
              boxSizing: "border-box"
            }} 
          />
          <input type="text" name="address" placeholder="Your Address" value={formData.address} onChange={handleChange} required 
            style={{
              width: "calc(100% - 20px)",
              maxWidth: "360px",
              padding: "5px",
              margin: "5px",
              border: "1px solid #ccc",
              borderRadius: "5px",
              fontSize: "16px",
              display: "block",
              boxSizing: "border-box"
            }} 
          />
          <textarea name="reason" placeholder="Why do you want to adopt?" value={formData.reason} onChange={handleChange} required 
            style={{
              width: "calc(100% - 20px)",
              maxWidth: "360px",
              padding: "5px",
              margin: "5px",
              border: "1px solid #ccc",
              borderRadius: "5px",
              fontSize: "16px",
              display: "block",
              boxSizing: "border-box",
              height: "80px",
              resize: "none"
            }} 
          />
          <button type="submit" 
            style={{
              width: "90%",
              background: "#5a3531",
              color: "white",
              border: "none",
              padding: "10px 15px",
              cursor: "pointer",
              borderRadius: "5px",
              fontSize: "16px",
              marginTop: "10px",
              marginLeft: "15px",
              textAlign: "center"
            }} 

            onMouseOver={(e) => e.target.style.background = "#5a3531ab"}
            onMouseOut={(e) => e.target.style.background = "#5a3531"}
          >
            Submit Request
          </button>
        </form>
      </div>
    </div>
  );
};

export default AdoptionRequestForm;
