// üìå Manage Services
const ServiceManagement = () => {
  const [services, setServices] = useState({ Grooming: [], Daycare: [] });
  const [newService, setNewService] = useState({ type: "Grooming", name: "", address: "", contact: "" });

  // Fetch services from the backend on mount
  useEffect(() => {
    axios.get("http://localhost:5000/api/services")
      .then((res) => {
        const grooming = res.data.filter(service => service.type.toLowerCase() === "grooming");
        const daycare = res.data.filter(service => service.type.toLowerCase() === "daycare");
        setServices({ Grooming: grooming, Daycare: daycare });
      })
      .catch((err) => console.error("Error fetching services:", err));
  }, []);

  // Handle adding a new service center
  const handleAddService = async () => {
    if (newService.name && newService.address && newService.contact) {
      try {
        const res = await axios.post("http://localhost:5000/api/services", newService);
        setServices(prevServices => ({
          ...prevServices,
          [newService.type]: [...prevServices[newService.type], res.data] // Add new center to correct category
        }));
        setNewService({ type: "Grooming", name: "", address: "", contact: "" }); // Reset form
      } catch (error) {
        console.error("Error adding service:", error);
      }
    }
  };

  // Handle deleting a service center
  const handleDeleteService = (serviceId, type) => {
    axios.delete(`http://localhost:5000/api/services/${serviceId}`)
      .then(() => {
        setServices(prevServices => ({
          ...prevServices,
          [type]: prevServices[type].filter(service => service._id !== serviceId)
        }));
      })
      .catch((err) => console.error("Error deleting service:", err));
  };

  return (
    <div className="service-management">
      <h2>Manage Services</h2>

      {/* Add Service Center Form */}
      <div className="service-form">
        <select value={newService.type} onChange={(e) => setNewService({ ...newService, type: e.target.value })}>
          <option value="Grooming">Grooming</option>
          <option value="Daycare">Daycare</option>
        </select>
        <input type="text" placeholder="Center Name" value={newService.name} onChange={(e) => setNewService({ ...newService, name: e.target.value })} />
        <input type="text" placeholder="Address" value={newService.address} onChange={(e) => setNewService({ ...newService, address: e.target.value })} />
        <input type="text" placeholder="Contact Number" value={newService.contact} onChange={(e) => setNewService({ ...newService, contact: e.target.value })} />
        <button onClick={handleAddService}>Add Center</button>
      </div>

      {/* Display Service Centers */}
      <div className="service-list-container">
        {["Grooming", "Daycare"].map((type) => (
          <div key={type} className="service-category">
            <h3>{type} Centers</h3>
            <ul className="service-list">
              {services[type].map((service) => (
                <li key={service._id} className="service-item">
                  <h4>{service.name}</h4>
                  <p>üìç {service.address}</p>
                  <p>üìû {service.contact}</p>
                  <button className="delete-btn" onClick={() => handleDeleteService(service._id, type)}>‚ùå Delete</button>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};



// üìå Product Management
const ProductManagement = () => {
    const [products, setProducts] = useState([]);
    const [newProduct, setNewProduct] = useState({ type: "Food", name: "", image: "", price: "" });
    const [selectedProducts, setSelectedProducts] = useState([]); // Track selected products for deletion
  
    useEffect(() => {
      axios.get("http://localhost:5000/api/products")
        .then((res) => setProducts(res.data))
        .catch((err) => console.error("Error fetching products:", err));
    }, []);
  
    const handleAddProduct = async () => {
      if (!newProduct.name || !newProduct.image || !newProduct.price || !newProduct.type) {
        console.error("All fields are required");
        return;
      }
    
      try {
        const response = await axios.post("http://localhost:5000/api/products", newProduct, {
          headers: { "Content-Type": "application/json" },
        });
    
        setProducts([...products, response.data]); // Add the new product to the list
        setNewProduct({ type: "Food", name: "", image: "", price: "" }); // Reset form
      } catch (error) {
        console.error("Error adding product:", error.response?.data || error.message);
      }
    };       

  // ‚úÖ Delete a single product
  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:5000/api/products/${productId}`);
      setProducts(products.filter((product) => product._id !== productId));
    } catch (error) {
      console.error("‚ùå Error deleting product:", error.response?.data || error.message);
    }
  };

  // ‚úÖ Bulk delete selected products
  const handleBulkDelete = async () => {
    if (selectedProducts.length === 0) {
      alert("‚ùå No products selected for deletion!");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/products/bulk-delete", { productIds: selectedProducts });
      setProducts(products.filter((product) => !selectedProducts.includes(product._id)));
      setSelectedProducts([]); // Clear selection after deletion
    } catch (error) {
      console.error("‚ùå Error deleting selected products:", error.response?.data || error.message);
    }
  };

  // ‚úÖ Handle Image Upload
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setNewProduct({ ...newProduct, image: reader.result });
      reader.readAsDataURL(file);
    }
  };

  return (
    <div>
      <h2>Manage Pet Products</h2>

      {/* Product Form */}
      <div className="product-form">
        <select value={newProduct.type} onChange={(e) => setNewProduct({ ...newProduct, type: e.target.value })}>
          <option value="Food">Food</option>
          <option value="Clothes">Clothes</option>
          <option value="Toys">Toys</option>
        </select>
        <input type="text" placeholder="Product Name" value={newProduct.name} onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })} />
        <input type="number" placeholder="Price" value={newProduct.price} onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })} />
        <input type="file" accept="image/*" onChange={handleImageUpload} />
        <button onClick={handleAddProduct}>Add Product</button>
      </div>

      {/* Product List */}
      <ul className="product-list">
        {products.map((product) => (
          <li key={product._id} className="product-item">
            <input
              type="checkbox"
              checked={selectedProducts.includes(product._id)}
              onChange={() =>
                setSelectedProducts((prev) =>
                  prev.includes(product._id) ? prev.filter((id) => id !== product._id) : [...prev, product._id]
                )
              }
            />
            <p>Type: {product.type}</p>
            <img src={product.image} alt={product.name} width="100" />
            <h3>{product.name}</h3>
            <p>Price: ‚Çπ{product.price}</p>
            <button onClick={() => handleDeleteProduct(product._id)}>Delete</button>
          </li>
        ))}
      </ul>

      {/* Bulk Delete Button */}
      {selectedProducts.length > 0 && <button onClick={handleBulkDelete}>Delete Selected</button>}
    </div>
  );
};



// üìå Adoption Requests Placeholder
const AdoptionRequests = () => {
  const [requests, setRequests] = useState([]);

  // Fetch adoption requests
  useEffect(() => {
    axios.get("http://localhost:5000/api/adoption-requests")
      .then(response => setRequests(response.data))
      .catch(error => console.error("Error fetching requests:", error));
  }, []);

  // Handle Approve/Reject
  const handleStatusChange = async (id, status) => {
    try {
      await axios.put(`http://localhost:5000/api/adoption-requests/${id}`, { status });
      setRequests(prevRequests =>
        prevRequests.map(req =>
          req._id === id ? { ...req, status } : req
        )
      );
    } catch (error) {
      console.error("Error updating request status:", error);
    }
  };

  return (
    <div className="adoption-requests-container">
  <h2>Adoption Requests</h2>
  {requests.length > 0 ? (
    <div className="requests-list">
      {requests.map((req, index) => (
        <div key={req._id} className="request-item">
          <h3>‚Ä¢ {index + 1} - {req.petName}</h3>
          <p><strong>User:</strong> {req.userName}</p>
          <p><strong>Email:</strong> {req.email}</p>
          <p><strong>Phone:</strong> {req.phone}</p>
          <p><strong>Reason:</strong> {req.reason}</p>
          <p><strong>Status:</strong> <span className={`status ${req.status || "pending"}`}>{req.status || "Pending"}</span></p>
          
          <div className="actions">
            <button className="approve" onClick={() => handleStatusChange(req._id, "approved")}>Approve</button>
            <button className="reject" onClick={() => handleStatusChange(req._id, "rejected")}>Reject</button>
          </div>
        </div>
      ))}
    </div>
  ) : (
    <p>No adoption requests available.</p>
  )}
</div>


);
};

export default AdminDashboard;
