import React, { useEffect, useState } from "react";

const AdoptionRequestForm = ({ pet, onClose }) => {
  const [formData, setFormData] = useState({
    petName: "",
    username: "",
    email: "",
    phone: "",
    address: "",
    reason: ""
  });

  useEffect(() => {
    if (pet) {
      setFormData((prev) => ({
        ...prev,
        petName: pet.name || ""
      }));
    }
  }, [pet]);

  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/adoption-requests", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...formData, petId: pet._id })
      });

      if (res.ok) {
        alert("✅ Adoption request submitted!");
        onClose();
      } else {
        alert("❌ Failed to submit request.");
      }
    } catch (error) {
      console.error("Submit error:", error);
      alert("❌ Server error");
    }
  };

  if (!pet) return null;

  return (
    <>
      {/* 🔹 Inline CSS Styling */}
      <style>{`
        .modal-overlay {
          position: fixed;
          top: 0; left: 0; right: 0; bottom: 0;
          background: rgba(0, 0, 0, 0.6);
          display: flex;
          justify-content: center;
          align-items: center;
          z-index: 9999;
        }
        .modal {
          background: white;
          padding: 10px;
          width: 70%;
          height: 450px;
          max-width: 500px;
          border-radius: 10px;
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
          position: relative;
        }
        .modal h2 {
          margin-bottom: 10px;
          text-align: center;
        }
        .modal label {
          display: block;
          margin: 10px 0 5px;
          font-weight: 500;
        }
        .modal input,
        .modal textarea {
          width: 100%;
          padding: 5px;
          border: 1px solid #ccc;
          border-radius: 5px;
          box-sizing: border-box;
        }
        .modal textarea {
          resize: none;
          height: 70px;
        }
        .form-actions {
          display: flex;
          justify-content: flex-end;
          margin-top: 20px;
          gap: 10px;
        }
        .cancel-btn,
        .submit-btn {
          padding: 8px 16px;
          border: none;
          border-radius: 5px;
          cursor: pointer;
        }
        .cancel-btn {
          background: #ccc;
          color: #333;
        }
        .submit-btn {
          background: #5a3531;
          color: white;
        }
        .close-button {
          position: absolute;
          top: 10px;
          right: 16px;
          font-size: 24px;
          background: none;
          border: none;
          color: #999;
          cursor: pointer;
        }
        .close-button:hover {
          color: #000;
        }
      `}</style>

      {/* 🔹 Modal HTML */}
      <div className="modal-overlay">
        <div className="modal">
          <button className="close-button" onClick={onClose}>&times;</button>
          <h2>Adoption Request for <span>{pet.name}</span></h2>

          <form onSubmit={handleSubmit}>
            <label>Pet Name</label>
            <input type="text" name="petName" value={formData.petName} readOnly />

            <label>Your Name</label>
            <input type="text" name="username" value={formData.username} onChange={handleChange} required />

            <label>Email</label>
            <input type="email" name="email" value={formData.email} onChange={handleChange} required />

            <label>Phone</label>
            <input type="text" name="phone" value={formData.phone} onChange={handleChange} required />

            <label>Address</label>
            <textarea name="address" value={formData.address} onChange={handleChange} required />

            <label>Reason for Adoption</label>
            <textarea name="reason" value={formData.reason} onChange={handleChange} required />

            <div className="form-actions">
              <button type="button" className="cancel-btn" onClick={onClose}>Cancel</button>
              <button type="submit" className="submit-btn">Submit</button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default AdoptionRequestForm;
