import React, { useState } from "react";
import axios from "axios";

function AddPetForm({ onPetAdded }) {
  const [newPet, setNewPet] = useState({
    name: "",
    type: "",
    breed: "",
    age: "",
    image: null, // Ensure image is handled
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewPet((prevPet) => ({ ...prevPet, [name]: value }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setNewPet((prevPet) => ({ ...prevPet, image: reader.result })); // Convert to Base64
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!newPet.name || !newPet.type || !newPet.breed || !newPet.age) {
      alert("Please fill in all details before adding a pet!");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/pets", newPet);
      alert("Pet added successfully!");
      setNewPet({ name: "",type: "", breed: "", age: "", image: null }); // Reset form
      onPetAdded(); // Refresh pet list
    } catch (error) {
      console.error("Error adding pet:", error);
      alert("Failed to add pet.");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        backgroundColor: "#5a3531ba",
        color: "white",
        padding: "15px",
        borderRadius: "10px",
        width: "250px",
        height: "400px",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: "10px",
      }}
    >
      <h3 style={{ marginBottom: "10px" }}>Add a New Pet</h3>
      <input
        type="text"
        name="name"
        value={newPet.name}
        placeholder="Pet Name"
        onChange={handleChange}
        required
        style={{
          width: "90%",
          padding: "8px",
          border: "1px solid #ddd",
          borderRadius: "5px",
          background: "#c7c7c7",
          color: "black",
        }}
      />
      {/* Dropdown for Pet Type */}
      <select
        name="type"
        value={newPet.type}
        onChange={handleChange}
        required
        style={{
          width: "97%",
          padding: "8px",
          border: "1px solid #ddd",
          borderRadius: "5px",
          background: "#c7c7c7",
          color: "black",
        }}
      >
        <option value="">Select Pet Type</option>
        <option value="Dog">Dog</option>
        <option value="Cat">Cat</option>
        </select>

      <input
        type="text"
        name="breed"
        value={newPet.breed}
        placeholder="Breed"
        onChange={handleChange}
        required
        style={{
          width: "90%",
          padding: "8px",
          border: "1px solid #ddd",
          borderRadius: "5px",
          background: "#c7c7c7",
          color: "black",
        }}
      />
      <input
        type="number"
        name="age"
        value={newPet.age}
        placeholder="Age"
        onChange={handleChange}
        required
        style={{
          width: "90%",
          padding: "8px",
          border: "1px solid #ddd",
          borderRadius: "5px",
          background: "#c7c7c7",
          color: "black",
        }}
      />
      <input
        type="file"
        accept="image/*"
        onChange={handleImageUpload}
        style={{
          width: "90%",
          padding: "5px",
          background: "#555",
          color: "white",
          borderRadius: "5px",
        }}
      />
      <button
        type="submit"
        style={{
          padding: "10px",
          background: "#5a3531",
          border: "none",
          color: "white",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Add Pet
      </button>
    </form>
  );
}

export default AddPetForm;
